<?php// Tracking impressions and conversionsadd_action('inbound_store_lead_pre','lp_set_conversion',10,1);function lp_set_conversion($data){	// Update Landing Page Conversions	if($data['post_type'] === 'landing-page' )	{					$disable_admin_tracking = get_option( 'lp-main-landing-page-disable-admin-tracking', '0' );				if ( empty($data['wpleads_email_address']) && $data['element_type'] == 'FORM' ) {			return; 		}				if ( $disable_admin_tracking && current_user_can( 'manage_options' ) ) {			return;		}				$lp_conversions = get_post_meta( $data['page_id'], 'lp-ab-variation-conversions-'.$data['variation'], true );		$lp_conversions++;		update_post_meta(  $data['page_id'], 'lp-ab-variation-conversions-'.$data['variation'], $lp_conversions );			} else {		$conversions = get_post_meta( $data['page_id'], '_inbound_conversions_count', true );		$conversions++;		update_post_meta(  $data['page_id'], '_inbound_conversions_count', $conversions );	}}?>